package main

import (
	"fmt"
)

func main() {
	fmt.Println(checkInclusion("ab", "sdsdabasdasd"))
	fmt.Println(checkInclusion("a", "ab"))
	fmt.Println(checkInclusion("ab", "eidboaoo"))
	fmt.Println(checkInclusion("hello", "ooolleoooleh"))
	fmt.Println(checkInclusion("",
		""))
}

func checkInclusion(s1 string, s2 string) bool {
	s1Len := len(s1)
	s2Len := len(s2)
	if s1Len > s2Len {
		return false
	}
	ms1 := map[string]int{}
	for _, s1c := range s1 {
		ms1[string(s1c)]++
	}

	ms2 := map[string]int{}
	s := s2[0:s1Len]
	for _, s2c := range s {
		ms2[string(s2c)]++
	}
	offset := 0
	for {
		if isPermutated(ms1, ms2) {
			return true
		} else {
			t := string(s2[offset])
			ms2[t]--
			offset++
		}
		if offset+s1Len > s2Len {
			break
		}
		next := string(s2[offset+s1Len-1])
		ms2[next]++
	}
	return false
}

func isPermutated(ms1 map[string]int, ms2 map[string]int) bool {
	for ms1n, ms1v := range ms1 {
		if ms2v, ok := ms2[ms1n]; !ok || ms2v != ms1v {
			return false
		}
	}
	return true
}
